mapper.select:
select SUM(b.use_status = 1) AS use_num,SUM(b.use_status = 2) AS not_use_num, a.id as coupon_id,a.name,a.enable,a.total_num,count(b.id) as receive_num,(a.total_num-count(b.id))as enable_num
from am_coupon as a
LEFT JOIN am_coupon_rel_coupon_user as b on a.id = b.coupon_id
where a.del_flag = 0

    and a.id = #{id}


    and a.enable = #{enable}


    and a.name like concat('%', #{name}, '%')


    and a.type = #{type}

group by a.id
having 1=1

    and use_num >= #{lowestUseNum}


    and use_num <= #{highestUseNum}


    and not_use_num >= #{lowestNotUseNum}


    and not_use_num <= #{highestNotUseNum}

select c.id as user_id,a.name as coupon_name,c.name as user_name,c.phone,b.id as order_id,d.use_status,d.receive_time,b.create_time as use_time
from am_coupon a
inner join um_user c
inner join am_coupon_rel_coupon_user d
left join om_order b on a.id = b.coupon_id and c.id = b.um_user_id
where  a.del_flag =0 and a.id =#{id} and d.user_id = c.id and d.coupon_id = a.id

        and c.id = #{userId}


        and c.phone = #{phone}


        and d.use_status = #{useStatus}

select a.id as rel_id,b.id,b.name,b.level from am_coupon_rel_coupon_goods a,pm_goods_category b where a.coupon_id = #{id} and a.association_id = b.id
and b.id = #{searchId}
and b.name like concat('%', #{name} ,'%')

select a.id as rel_id,b.id,b.name,b.goods_category_id from am_coupon_rel_coupon_goods a,pm_goods b where a.coupon_id = #{id} and a.association_id = b.id
and b.id = #{searchId}
and b.name like concat('%', #{name} ,'%')

select DISTINCT a.id as goods_id,a.name as goods_name,b.id as category_id,b.name as category_name
FROM pm_goods a
INNER JOIN pm_goods_category b ON a.goods_category_id = b.id
WHERE a.del_flag = 0 AND b.del_flag = 0 AND a.publish_status = 1
AND NOT EXISTS (
SELECT 1 FROM am_coupon_rel_coupon_goods g ,am_coupon h where  h.id=g.coupon_id and  g.association_id = a.id  and h.enable = 1
);

    and a.id = #{t.goodsId}


    and a.name like concat('%', #{t.goodsName}, '%')


    and b.id = #{t.categoryId}


    and b.name like concat('%', #{t.categoryName}, '%')

select u.id couponRelUserId,c.level_id,s.id skuId,s.sell_price sellPrice,c.id couponId,c.name,u.receive_time,dead_line,c.type, c.discount,c.discount_full_money,c.reduction_full_money,c.reduction_post_money FROM am_coupon c,am_coupon_rel_coupon_goods cg,am_coupon_rel_coupon_user u,pm_goods_sku s where c.del_flag=0 and s.del_flag=0 and c.id=cg.coupon_id and c.id=u.coupon_id and cg.association_id=s.goods_id and u.dead_line>=NOW() and c.`enable`=1 and u.use_status=2 and u.user_id=#{userId} and s.id in
#{skuId}
and u.id=#{couponRelUserId}
