mapper.select:
select a.id as activity_id,a.name as activity_name,b.id as activityGoodsRelId,b.activity_type ,c.id as goods_id,c.name as goods_name,b.verify_status, b.modify_cause,b.refuse_case,d.name as brand,c.store_id,f.name as store_name,c.icon as goods_picture from ${tableName} a inner join am_activity_rel_activity_goods b on a.id = b.activity_id and b.del_flag = 0 inner join pm_goods c on b.goods_id = c.id and c.publish_status = 1 and c.del_flag = 0 inner join pm_goods_rel_attribute_good e on e.goods_good_id = c.id and e.del_flag = 0 inner join pm_goods_attribute d on d.type = 8 and e.goods_attribute_id = d.id and d.del_flag = 0 inner join sm_store f on c.store_id = f.id and f.del_flag = 0 where a.del_flag = 0
and f.id= #{storeId}
and b.verify_status != 7
and f.id= #{t.storeId}
and f.name like concat('%', #{t.activityId} ,'%')
and c.id= #{t.goodsId}
and b.verify_status= #{t.verifyStatus}
and c.name like concat('%', #{t.verifyStatus} ,'%')
and a.name like concat('%', #{t.activityName} ,'%')
