mapper.select:
WITH RECURSIVE childrenIds (id) AS ( SELECT id FROM ${tableName} WHERE del_flag=0 and
parent_id is null
parent_id=#{parentId}
UNION ALL SELECT c.id FROM childrenIds AS p JOIN ${tableName} AS c ON p.id = c.parent_id where c.del_flag=0 ) SELECT id FROM childrenIds
UNION SELECT #{parentId}
