mapper.select:
SELECT `mara`.id AS relId, `aag`.id AS groupId, `aag`.name AS groupName, `aag`.picture AS groupPicture FROM mm_advice `ma` LEFT JOIN mm_advice_rel_associaiton `mara` ON `mara`.`advice_id` = `ma`.id LEFT JOIN am_activity_group `aag` ON `aag`.id = `mara`.`association_id`
AND `ma`.location = #{location} AND `ma`.`enabled` = 1 AND `mara`.del_flag = 0 AND `aag`.`enable` = 1 AND `aag`.`del_flag`= 0 AND `aag`.`type` = #{groupType}
ORDER BY `aag`.create_time DESC

SELECT `pg`.id AS goodsId, `pg`.name AS goodsName, `pg`.icon, `pg`.sales_volume, `pgs`.sell_price, `pgs`.line_price, ar.reduction_full_money, ar.reduction_post_money, GROUP_CONCAT(`pga`.name) as labels FROM am_reduced ar LEFT JOIN am_activity_rel_activity_goods aarag ON ar.id = aarag.`activity_id` AND ar.group_id = #{groupId} LEFT JOIN pm_goods pg ON pg.id = aarag.`goods_id` LEFT JOIN pm_goods_sku `pgs` ON `pg`.id = `pgs`.goods_id
LEFT JOIN pm_goods_category pgc ON pgc.id = pg.goods_category_id LEFT JOIN pm_goods_category pgc1 ON pgc1.id = pgc.parent_id
LEFT JOIN pm_goods_rel_attribute_good pgrag ON pg.id = pgrag.`goods_good_id` LEFT JOIN pm_goods_attribute pga ON pgrag.goods_attribute_id = pga.`id`
ar.group_id = #{groupId} AND ar.`del_flag` = 0 AND ar.enable = 1 AND ar.`activity_end_time` > NOW() AND ar.`activity_start_time` < NOW() AND aarag.`verify_status` = 3 AND aarag.`del_flag` = 0 AND pg.`publish_status` = 1
AND pgc1.parent_id = #{categoryId}
AND `pgs`.del_flag = 0 AND `pgs`.id = ( SELECT temp.id FROM pm_goods_sku temp WHERE temp.goods_id = pg.id AND temp.del_flag = 0 ORDER BY sell_price LIMIT 1 ) AND pgrag.`del_flag` = 0 AND pga.`del_flag` = 0 AND pga.`enabled` = 1 AND pga.`type` = 5
GROUP BY pg.id,pgs.id,ar.id
ORDER BY pgs.sell_price ${sortWay.name}
ORDER BY pg.sales_volume ${sortWay.name}
ORDER BY pg.comprehensive_score DESC

SELECT `pg`.id AS goodsId, `pg`.name AS goodsName, `pg`.icon, `pg`.sales_volume, `pgs`.sell_price, `pgs`.line_price, `ai`.discount_price_ratio FROM am_integrals ai LEFT JOIN am_activity_rel_activity_goods aarag ON ai.id = aarag.`activity_id` AND ai.group_id = #{groupId} LEFT JOIN pm_goods pg ON pg.id = aarag.`goods_id` LEFT JOIN pm_goods_sku `pgs` ON `pg`.id = `pgs`.goods_id
LEFT JOIN pm_goods_category pgc ON pgc.id = pg.goods_category_id LEFT JOIN pm_goods_category pgc1 ON pgc1.id = pgc.parent_id
ai.group_id = #{groupId} AND ai.`del_flag` = 0 AND ai.enable = 1 AND ai.`activity_end_time` > NOW() AND ai.`activity_start_time` < NOW() AND aarag.`verify_status` = 3 AND aarag.`del_flag` = 0 AND pg.`publish_status` = 1
AND pgc1.parent_id = #{categoryId}
AND `pgs`.del_flag = 0 AND `pgs`.id = ( SELECT temp.id FROM pm_goods_sku temp WHERE temp.goods_id = pg.id AND temp.del_flag = 0 ORDER BY sell_price LIMIT 1 )
ORDER BY pgs.sell_price ${sortWay.name}
ORDER BY pg.sales_volume ${sortWay.name}
ORDER BY pg.comprehensive_score DESC

select id as advice_id,name,location,enabled,picture from mm_advice where del_flag = 0
and enabled = #{enabled}
and name like concat('%', #{name}, '%')
and location = #{location}
order by create_time desc

select id as value,name as label
from mm_advice
where del_flag = 0 and location = #{location}

select a.id,a.name
from mm_advice_tab a,mm_advice_rel_tab b
where a.del_flag = 0 and b.del_flag = 0 and a.id = b.tab_id and b.advice_id = #{adviceId}

select a.name as condition_name,b.id as goods_id,b.name as goods_name,b.subtitle,b.icon as picture,b.activity_cost_rate,
b.profits_rate,b.sales_volume,d.sell_price as sell_price,d.line_price,b.forward_num,b.collection_num
from pm_goods_attribute a,pm_goods b,pm_goods_rel_attribute_good c,pm_goods_sku	d
where b.publish_status = 1 and a.del_flag = 0 and b.del_flag = 0 and c.del_flag =0 and d.del_flag =0 and a.id = #{conditionId}
and c.goods_attribute_id = a.id and c.goods_good_id = b.id and d.goods_id = b.id
and d.id = (SELECT id from pm_goods_sku d2 where d2.`goods_id` = b.id  and d2.del_flag =0 and d2.sell_price =
(SELECT Min(d1.sell_price) FROM pm_goods_sku d1 WHERE d1.`goods_id` = b.id  and d1.del_flag =0 LIMIT 1) limit 1)

    and sell_price >= #{lowestPrice}


    and sell_price <= #{highestPrice}



    order by sell_price ${sortWay.name}


    order by b.comprehensive_score ${sortWay.name}


    order by b.sales_volume ${sortWay.name}


    order by b.collection_num ${sortWay.name}

select c.name as condition_name,b.id as goods_id,b.name as goods_name,b.icon as picture,b.subtitle,b.activity_cost_rate,
b.profits_rate,b.sales_volume,d.sell_price as sell_price,d.line_price,b.forward_num,b.collection_num
from mm_advice_rel_tab_things a,pm_goods b,pm_goods_sku	d,mm_advice_tab c
where b.publish_status = 1 and a.del_flag = 0 and b.del_flag = 0 and c.del_flag =0 and d.del_flag =0 and a.tab_id = #{conditionId}
and a.association_id = b.id and d.goods_id=b.id and c.id = a.tab_id
and d.id = (SELECT id from pm_goods_sku d2 where d2.`goods_id` = b.id  and d2.del_flag =0 and d2.sell_price =
(SELECT Min(d1.sell_price) FROM pm_goods_sku d1 WHERE d1.`goods_id` = b.id  and d1.del_flag =0 LIMIT 1) limit 1)

    and sell_price >= #{lowestPrice}


    and sell_price <= #{highestPrice}


    order by sell_price ${sortWay.name}


    order by b.comprehensive_score ${sortWay.name}


    order by b.sales_volume ${sortWay.name}


    order by b.collection_num ${sortWay.name}

select c.name as condition_name,b.id as goods_id,b.name as goods_name,b.icon as picture,b.activity_cost_rate,
b.profits_rate,b.sales_volume,d.sell_price as sell_price,d.line_price,b.forward_num,b.collection_num
from pm_goods b,pm_goods_sku d,pm_goods_category c
where  b.publish_status = 1 and b.del_flag = 0 and d.del_flag =0 and c.del_flag = 0 and c.id = #{conditionId} and b.goods_category_id = #{conditionId} and d.goods_id=b.id
and d.id = (SELECT id from pm_goods_sku d2 where d2.`goods_id` = b.id  and d2.del_flag =0 and d2.sell_price =
(SELECT Min(d1.sell_price) FROM pm_goods_sku d1 WHERE d1.`goods_id` = b.id  and d1.del_flag =0 LIMIT 1) limit 1)

    and sell_price >= #{lowestPrice}


    and sell_price <= #{highestPrice}


    order by sell_price ${sortWay.name}


    order by b.comprehensive_score ${sortWay.name}


    order by b.sales_volume ${sortWay.name}


    order by b.collection_num ${sortWay.name}

select distinct b.id as goods_id,b.name as goods_name,b.icon as picture,b.activity_cost_rate,
b.profits_rate,b.sales_volume,d.sell_price as sell_price,d.line_price,b.forward_num,b.collection_num,b.comprehensive_score
from pm_goods b,pm_goods_sku d,pm_goods_category c,mm_advice_rel_associaiton a
where a.del_flag = 0 and b.publish_status = 1 and b.del_flag = 0 and d.del_flag =0 and c.del_flag = 0 and a.association_id = c.id and d.goods_id=b.id
and a.advice_id = #{adviceId}
and d.id = (SELECT id from pm_goods_sku d2 where d2.`goods_id` = b.id  and d2.del_flag =0 and d2.sell_price =
(SELECT Min(d1.sell_price) FROM pm_goods_sku d1 WHERE d1.`goods_id` = b.id  and d1.del_flag =0 LIMIT 1) limit 1)


    and sell_price >= #{lowestPrice}


    and sell_price <= #{highestPrice}


    order by sell_price ${sortWay.name}


    order by b.comprehensive_score ${sortWay.name}


    order by b.sales_volume ${sortWay.name}


    order by b.collection_num ${sortWay.name}

select b.id as goods_id,b.name as goods_name,b.icon as picture,b.activity_cost_rate,
b.profits_rate,b.sales_volume,d.sell_price as sell_price,d.line_price,b.forward_num,b.collection_num
from pm_goods b,pm_goods_sku d,am_coupon_rel_coupon_goods a
where b.publish_status = 1 and b.del_flag = 0 and d.del_flag =0 and a.association_id = b.id and d.goods_id=b.id
and a.coupon_id = #{conditionId}
and d.id = (SELECT id from pm_goods_sku d2 where d2.`goods_id` = b.id  and d2.del_flag =0 and d2.sell_price =
(SELECT Min(d1.sell_price) FROM pm_goods_sku d1 WHERE d1.`goods_id` = b.id  and d1.del_flag =0 LIMIT 1) limit 1)


    and sell_price >= #{lowestPrice}


    and sell_price <= #{highestPrice}


    order by sell_price ${sortWay.name}


    order by b.comprehensive_score ${sortWay.name}


    order by b.sales_volume ${sortWay.name}


    order by b.collection_num ${sortWay.name}

