mapper.select:
select mi.*, mil.`name` as infoLabelName, mic.`name` as infoCategoryName
from mm_information mi left join mm_information_label mil on (mi.info_label_id = mil.id and mil.del_flag = 0)
    left join mm_information_category mic on (mi.info_category_id = mic.id and mic.del_flag = 0)
where mi.id = #{id}

select mi.id, mi.title, mi.enabled, mi.sort, mi.verify_status, mi.update_time, mi.browsing_num, mi.forward_num, mi.comment_num, mi.liked_num, mi.collection_num, mil.name as infoLabelName, mic.name as infoCategoryName from mm_information mi left join mm_information_label mil on mil.id = mi.info_label_id left join mm_information_category mic on mic.id = mi.info_category_id
mi.del_flag = 0 and mil.del_flag = 0 and mil.del_flag = 0
and mi.store_id = #{storeId}
and mi.id = #{id}
and date(mi.create_time) >= #{startTime}
and date(mi.create_time) <= #{endTime}
and mi.title like concat('%', #{name}, '%')
order by mi.sort desc

select mi.id, mi.title, LEFT(mi.pure_text,100) AS pureText , mi.`cover_image`, mi.`forward_num`, mi.`comment_num`, mi.`liked_num`, mi.update_time, ss.`id` AS storeId, ss.`logo_image`, ss.`name` AS storeName, ( SELECT GROUP_CONCAT(`ssl`.name) FROM sm_store_label `ssl` ,sm_store_rel_label ssrl WHERE `ssrl`.store_label_id = `ssl`.id AND `ssrl`.store_id = ss.`id` AND `ssl`.del_flag = 0 ) AS storeLabels, ss.collection_num, ( SELECT COUNT(mi1.liked_num) FROM mm_information mi1 WHERE mi1.store_id = ss.id ) AS likedSum, (EXISTS (SELECT 1 FROM um_user_favorites uuf WHERE uuf.favorites_id = ss.id AND uuf.`type` = '店铺' AND uuf.user_id = #{userId} AND uuf.del_flag = 0) ) AS is_focus, (EXISTS (SELECT 1 FROM mm_information_comment mic WHERE mic.info_id = mi.id AND mic.user_id = #{userId} ) ) as isComment, (EXISTS (SELECT 1 FROM mm_information_liked mil WHERE mil.info_id = mi.id AND mil.user_id = #{userId} AND mil.del_flag = 0 ) ) as isLiked, (EXISTS (SELECT 1 FROM mm_information_forward mif WHERE mif.info_id = mi.id AND mif.user_id = #{userId} ) ) as isForward from mm_information mi LEFT JOIN sm_store ss ON mi.`store_id` = ss.id
left join um_user_favorites uuf on mi.store_id = uuf.favorites_id
mi.del_flag = 0 and mi.verify_status = 3
and mi.store_id = #{storeId}
and mi.info_label_id = #{infoLabelId}
and mi.info_category_id = #{infoCategoryId}
and (mi.title like concat('%', #{keyword}, '%') or mi.pure_text like concat('%', #{keyword}, '%'))
and uuf.del_flag = 0 and uuf.user_id = #{userId}
GROUP BY mi.id,ss.id
order by mi.sort desc
order by mi.browsing_num desc
