mapper.select:
select a.photo as icon,a.name as nick_name,b.content,b.picture,b.sku_id,b.id from um_user a,om_evaluate b,om_order c
c.id = b.order_id and c.um_user_id = a.id and c.evaluate_status = 2 and isNull(b.parent_id)
and c.um_user_id = #{userId}

        select b.id as user_id,b.name as nick_name,b.phone,c.name as goods_name,e.sku_id,e.id as evaluate_id,e.description_start_level,
               e.picture,e.content,e.create_time,a.id as order_id
        from om_order a,um_user b,pm_goods c,pm_goods_sku d,om_evaluate e,sm_store f
         a.evaluate_status = 2 and a.id = e.order_id and e.sku_id = d.id and d.goods_id = c.id and a.um_user_id = b.id
		      and ISNULL(e.parent_id) and a.store_id = f.id

                  and a.store_id = #{evaluate.storeId}


                  and b.phone = #{evaluate.phone}


                  and b.id = #{evaluate.userId}


                  and e.order_id =#{evaluate.orderId}


                  and d.goods_id = #{evaluate.goodsId}


                  and c.name like concat ('%', #{evaluate.goodsName} ,'%')


                  and DATE_FORMAT(e.create_time,'%Y-%m-%d')=#{evaluate.evaluateTime}





        order by e.create_time
