mapper.select:
SELECT oob.id,
        oob.year,
        oob.`month_day`,
        ss.`name` as storeName,
        oob.`total_amount`,
        oob.`bill_status`,
        oob.`bill_status` as billStatusName,
        oob.`create_time`,
        oob.`withdrawal_time`,
        oob.`update_time`
FROM om_order_bill oob
LEFT JOIN sm_store ss
ON ss.id = oob.store_id

    oob.bill_type = #{billType}

        AND oob.store_id = #{storeId}


        AND oob.year = #{year}


        AND oob.month_day like concat(#{monthDay}, '%')


        AND oob.bill_status = #{billStatus}


        AND date(oob.create_time) >=   #{startTime}


        AND date(oob.create_time) <=  #{endTime}


        AND oob.total_amount  >=   #{minTotalAmount}


        AND oob.total_amount  <=  #{maxTotalAmount}


ORDER BY oob.create_time DESC

        SELECT COUNT(*)
        FROM sm_store ss

            DATE(ss.`create_time`)  <=  #{endDate}

                AND ss.`id` = #{storeId}

            AND  (

                    SELECT TIMESTAMPDIFF(WEEK,temp.`end_date`,#{endDate}) - ss.`payment_bill_settlement_cycle` = 0


                    SELECT TIMESTAMPDIFF(WEEK,temp.`end_date`,#{endDate}) - ss.`income_bill_Settlement_cycle` = 0

                FROM (
	                SELECT oob.`end_date`
	                FROM om_order_bill oob
	                WHERE oob.`store_id` = ss.`id`
	                AND oob.`bill_type` = #{billType}
	                AND oob.`end_date` <=  #{endDate}
	                ORDER BY end_date DESC
	                LIMIT 1
	            ) AS temp
	        )

        SELECT ss.id
        FROM sm_store ss

            DATE(ss.`create_time`)  <=  #{endDate}

                AND ss.`id` = #{storeId}

            AND  (

                    SELECT TIMESTAMPDIFF(WEEK,temp.`end_date`,#{endDate}) - ss.`payment_bill_settlement_cycle` = 0


                    SELECT TIMESTAMPDIFF(WEEK,temp.`end_date`,#{endDate}) - ss.`income_bill_Settlement_cycle` = 0

	            FROM (
	                SELECT oob.`end_date`
	                FROM om_order_bill oob
                    WHERE oob.`store_id` = ss.`id`
                    AND oob.`bill_type` = #{billType}
                    AND oob.`end_date` <=  #{endDate}
            	    ORDER BY end_date DESC
	                LIMIT 1
	            ) AS temp
	        )

SELECT oob.id, ss.`name`, oob.`total_amount`, oob.`total_num`, oob.`create_time`, FROM om_bill_rel_store obrs LEFT JOIN om_order_bill oob ON obrs.bill_id = oob.id LEFT JOIN sm_store ss ON ss.id = oob.store_id
obrs.store_id = #{storeId}
AND obrs.`bill_id` = #{billId}
AND oob.`total_amount` >= #{minAmount}
AND oob.`total_amount` <= #{maxAmount}
ORDER BY obrs.create_time DESC

SELECT ogt.order_id, ogt.goods_id, ogt.`name` as goodsName, ogt.`standard_str`, ogt.`article_number`, ogt.`number` as goodsNumber, ogt.`supplier_price`, ogt.`sell_price`, obrgt.`total_amount`, oo.create_time as createTime FROM om_goods_temp ogt LEFT JOIN om_bill_rel_goods_temp obrgt ON ogt.id = obrgt.`goods_temp_id` LEFT JOIN om_order oo ON oo.id = ogt.`order_id`
obrgt.bill_id = #{id}
ORDER BY ogt.create_time DESC
