mapper.select:
SELECT
    oal.id,
    po.pay_order_no,
    oal.om_rel_id,
    uu.`id` AS umUserId,
    uu.phone,
    oal.`log_type`,
    oal.`log_matter`,
    ABS(oal.amount) AS totalAmount,
    oal.payment_way,
    oal.arrival_way,
    oal.`create_time`
from om_account_log oal
left join pay_order po
on oal.pay_order_id = po.id
left join um_user uu
on uu.id = po.um_user_id

    oal.user_type = #{userType}

        and oal.om_rel_id = #{omRelId}


        and po.pay_order_no = #{payOrderNo}


        and oal.id = #{logId}


        and po.um_user_id = #{umUserId}


        and oal.log_type = #{logType}


        and oal.log_matter = #{logMatter}


        and uu.phone = #{phone}


        and date(oal.create_time) >=   #{startTime}


        and date(oal.create_time) <=  #{endTime}


        and ABS(oal.amount) >=   #{minAmount}

    F
        and ABS(oal.amount) <=  #{maxAmount}


order by oal.create_time desc

SELECT ouw.id, ouw.`um_user_id`, uu.`name`, uu.`phone`, ouw.`withdrawal_way`, ouw.`withdrawal_amount`, ouw.`actual_amount`, ouw.`alipay`, ouw.`wechat`, ouw.`real_name`, ouw.`withdrawal_status`, ouw.`create_time` FROM om_user_withdrawal ouw LEFT JOIN um_user uu ON ouw.`um_user_id` = uu.`id`
and ouw.um_user_id = #{userId}
and ouw.withdrawal_status = #{withdrawalStatus}
and uu.name like concat('%',#{name},'%')
and uu.phone like concat('%',#{phone},'%')
and date(ouw.create_time) >= #{startTime}
and date(ouw.create_time) <= #{endTime}
order by ouw.create_time

SELECT
    oal.id,
    oal.om_rel_id,
    oal.`log_type`,
    oal.`log_matter`,
    ABS(oal.amount) AS totalAmount,
    oal.`create_time`
FROM om_account_log oal

    oal.user_type = #{userType}
    and user_id = #{storeId}

        and oal.om_rel_id = #{omRelId}


        and oal.id = #{logId}


        and oal.log_matter = #{logMatter}


        and oal.log_type = #{logType}


        and date(oal.create_time) >=   #{startTime}


        and date(oal.create_time) <=  #{endTime}


        and ABS(oal.amount) >=   #{minAmount}


        and ABS(oal.amount) <=  #{maxAmount}

SELECT
    (
    SELECT sum(oal.amount)
    FROM om_account_log oal
    where oal.user_id = #{userId}
    AND account_type = #{accountType}
    AND DATE_FORMAT(create_time, '%Y-%m')  = #{logDate}
    AND oal.log_type = '收入'
    ) as income,
    (
    SELECT ABS(sum(oal.amount))
    FROM om_account_log oal
    where oal.user_id = #{userId}
    AND account_type = #{accountType}
    AND DATE_FORMAT(create_time, '%Y-%m')  = #{logDate}
    AND oal.log_type = '支出'
    ) as consume
