mapper.select:
SELECT a.id FROM `pm_goods_attribute` a, pm_goods_category c, pm_goods_rel_attribute_category ac WHERE a.del_flag = 0 AND c.del_flag = 0 AND ac.del_flag = 0 AND a.NAME in
#{name}
AND a.id = ac.goods_attribute_id AND c.id = ac.goods_category_id AND c.id = #{cId} and a.type=#{type}

SELECT a.id id,a.name name,IF(ac.id is null,false,true) is_select FROM pm_goods_attribute a left join pm_goods_rel_attribute_category ac on a.id = ac.goods_attribute_id
and ac.goods_category_id=#{t.categoryId}
where `type`=#{t.type}
and a.name like concat('%',#{t.name},'%')
and if(ac.del_flag is null ,1=1,ac.del_flag = 0) and a.del_flag=0

WITH RECURSIVE childrenIds (id,name,sort,icon,enabled,level,parent_id) AS ( (SELECT id,name,sort,icon,enabled,level,parent_id FROM pm_goods_category WHERE del_flag=0 and level=1
and name like concat('%',#{t.name},'%')
and id=#{t.id}
and enabled=#{t.enabled}
order by sort desc limit #{offset},#{pageSize} ) UNION ALL SELECT c.id,c.name,c.sort,c.icon,c.enabled,c.level,c.parent_id FROM childrenIds AS p JOIN pm_goods_category AS c ON p.id = c.parent_id where c.del_flag=0 ) SELECT id,name,sort,icon,enabled,level,parent_id FROM childrenIds

SELECT count(*) FROM pm_goods_category WHERE del_flag=0 and level=1
and name like concat('%',#{t.name},'%')
and id=#{t.id}
and enabled=#{t.enabled}
