mapper.select:
select b.id,b.value as name,b.is_custom from pm_goods_rel_attribute_value_sku a ,pm_goods_attribute_value b, pm_goods_attribute c,pm_goods_sku d
a.goods_attribute_value_id = b.id and b.product_attribute_id = c.id and a.del_flag = 0 and d.id = a.goods_sku_id
and c.id = #{attributeId}
and d.goods_id=#{goodsId}
GROUP BY b.id

SELECT a.store_id,f.name as storeName,a.id,a.icon,a.NAME as good_name,a.sort,a.sales_volume,a.star_status,a.publish_status,a.verify_status,
Min(b.supplier_price) AS supplier_price, Min(b.sell_price) AS sell_price,d.name as brandName
from pm_goods a
LEFT JOIN pm_goods_sku b on a.id = b.goods_id and b.del_flag = 0
LEFT JOIN pm_goods_rel_attribute_good c on c.del_flag = 0 and c.goods_good_id = a.id
inner JOIN pm_goods_attribute d on c.goods_attribute_id = d.id and d.type=8
inner join pm_goods_category e on e.id = a.goods_category_id
left join sm_store f on f.id = a.store_id and f.del_flag = 0


     and a.name like concat('%', #{goodName}, '%')


     and e.name like concat('%', #{categoryName}, '%')


    and d.name like concat('%', #{brandName}, '%')


    and a.verify_status = #{verifyStatus}


    and a.publish_status=#{publishStatus}


    and f.name like concat('%', #{storeName}, '%')


        and f.id =#{storeId}


        and b.sell_price >=#{lowestSellPrice}


        and b.sell_price <=#{highestSellPrice}


        and b.supplier_price >=#{lowestSupplierPrice}


        and b.supplier_price <=#{highestSupplierPrice}


GROUP BY a.id,d.id order by a.create_time

SELECT mrig.id, pg.`name`, pg.goods_category_id, Min(pgs.sell_price) AS sell_price,pg.goods_type FROM mm_information_rel_goods mrig LEFT JOIN pm_goods pg on (pg.id = mrig.goods_id and pg.del_flag = 0) LEFT JOIN pm_goods_sku pgs on (pg.id = pgs.goods_id and pgs.del_flag = 0)
mrig.information_id = #{id}
GROUP BY pg.id

SELECT pgrst.id as relId , pg.id as goodsId,pg.icon,pg.NAME as good_name,pg.sort,pg.sales_volume,pg.star_status, Min(pgs.supplier_price) AS supplier_price, Min(pgs.sell_price) AS sell_price,pga.`name` as brandName from pm_goods_rel_stock_template pgrst left join pm_goods pg on pg.id = pgrst.goods_id and pg.del_flag = 0 LEFT JOIN pm_goods_sku pgs on pg.id = pgs.goods_id LEFT JOIN pm_goods_rel_attribute_good pgrag on pgrag.del_flag = 0 and pgrag.goods_good_id = pg.id inner JOIN pm_goods_attribute pga on pgrag.goods_attribute_id = pga.id and pga.type=8 inner join pm_goods_category pgc on pgc.id = pg.goods_category_id left join sm_store ss on ss.id = pg.store_id and ss.del_flag = 0
pgrst.stock_template_id = #{id}
group by pgrst.id,pga.id

SELECT pg.store_id, pg.id AS goodsId, pg.`name` AS goodsName, MIN(pgs.sell_price) AS sellPrice, MIN(pgs.line_price) AS linePrice, SUM(pgs.`sales_volume`) AS salesVolume, pg.icon FROM pm_goods pg LEFT JOIN pm_goods_sku pgs ON pgs.goods_id = pg.id
LEFT JOIN pm_goods_category pgc3 ON pgc3.id = pg.goods_category_id LEFT JOIN pm_goods_category pgc2 ON pgc2.id = pgc3.parent_id AND pgc3.del_flag = 0 LEFT JOIN pm_goods_category pgc1 ON pgc1.id = pgc2.parent_id AND pgc2.del_flag = 0
pg.store_id = #{storeId} AND pg.verify_status = 3 AND pg.del_flag = 0 AND pgs.del_flag = 0
AND pgc1.id = #{goodsCategoryId} AND pgc1.del_flag = 0
AND DATEDIFF(now(), pg.update_time) > #{newGoodsDays}
AND pg.recommand_status = 1
AND pg.star_status = 1
GROUP BY pg.id
ORDER BY salesVolume*5 ${sortFileEnum.name()}
ORDER BY pg.`sales_volume` ${sortFileEnum.name()}
ORDER BY sellPrice ${sortFileEnum.name()}
ORDER BY pg.sort desc
ORDER BY pg.update_time desc

select id,name,create_time import_time from pm_goods where is_excel=true and del_flag=0
and id=#{id}
and name like concat('%', #{name}, '%')

select id,name
from pm_goods
where id in
  #{item}


    and id = #{id}


    and name like concat('%', #{name}, '%')

select DISTINCT a.icon,a.id as goods_id,a.name as goods_name,c.name as brand_name from pm_goods a,am_activity_rel_activity_category b,pm_goods_attribute c,pm_goods_rel_attribute_good d where b.activity_id = #{activityId} and b.del_flag= 0 and a.del_flag =0 and c.del_flag = 0 and d.del_flag = 0 and a.store_id = #{storeId} and b.category_id = a.goods_category_id and c.type = 8 and c.id = d.goods_attribute_id and d.goods_good_id = a.id and a.goods_category_id in #{item}
and a.id = #{t.goodsId}
and a.name like concat('%', #{t.goodsName} ,'%')
and c.name like concat('%', #{t.brandName} ,'%')
