mapper.select:
select id,phone,name,true_name,id_card,front_photo,back_photo
from um_user
where del_flag=0
and true_name is not null

    and id=#{userId}



    and phone like concat('%', #{phone}, '%')


    and enabled=#{enabled}


    and recent_login_time>=#{startRecentLoginTime}


    and recent_login_time<=#{endRecentLoginTime}
